mapper.select:
select distinct c.category_id as id, c.parent_id as parentId, c.name from category c left join part p on p.category_id = c.category_id left join attribute a on a.part_id = p.part_id
c.account_id = #{account}
and (
(lower(a.name) like '%${term}%' or lower(a.val) like '%${term}%' or lower(p.part_no) like '%${term}%')
)
order by c.name

select distinct p.part_id as id, p.category_id as category, p.part_no as number, p.description, p.notes, p.available, p.minimum from category c inner join part p on c.category_id = p.category_id left join attribute a on p.part_id = a.part_id
c.category_id = #{category}
and (
(lower(a.name) like '%${term}%' or lower(a.val) like '%${term}%' or lower(p.part_no) like '%${term}%')
)
order by p.part_no

select category_id as id, name
from category
where name = #{name} and account_id = #{account.id}

mapper.insert:

select coalesce(max(part_id),0) + 1 from part

insert into part (part_id, category_id, available, minimum, part_no, description, notes)
select #{part.id}, category_id, #{part.available}, #{part.minimum}, #{part.number}, #{part.description}, #{part.notes}
from category where category_id = #{part.category} and account_id = #{account}

mapper.update:
update part
category_id = #{part.category}, available = #{part.available}, minimum = #{part.minimum}, part_no = #{part.number}, description = #{part.description}, notes = #{part.notes},
where part_id = #{part.id}

update attribute
name = #{attribute.name}, value = #{attribute.value}, href = #{attribute.href}, sort = #{attribute.sort},
where attribute_id = #{attribute.id}

mapper.select:
select account_id as id, email, secret, created_at as createdAt, modified_at as modifiedAt
from account
where identifier = #{identifier}
