mapper.select:
select distinct c.category_id as id, c.parent_id as parentId, c.name from category c left join part p on p.category_id = c.category_id left join attribute a on a.part_id = p.part_id
c.account_id = #{account}
and (
(lower(a.name) like '%${term}%' or lower(a.val) like '%${term}%' or lower(p.part_no) like '%${term}%')
)
order by c.name

select category_id as id, name
from category
where name = #{name} and account_id = #{account.id}

mapper.update:
update part
category_id = #{part.category}, available = #{part.available}, minimum = #{part.minimum}, part_no = #{part.number}, description = #{part.description}, notes = #{part.notes}, modified_at = CURRENT_TIMESTAMP
where part_id = #{part.id}

update attribute 
set data = lo_create(-1)
where attributeId = #{attribute}

mapper.select:
select val as value, attribute_id as id, mime_type as mimeType, data, created_at as createdAt, modified_at as modifiedAt
from attribute
where attribute_id = #{attribute}

select account_id as id, email, secret, created_at as createdAt, modified_at as modifiedAt
from account
where identifier = #{identifier}
